const sony = require("../../bot/client");
const { MessageMedia } = require("whatsapp-web.js");
const th = require("consola");

const MEDIA_PATH = './src/assets/img/morada.jpg';

function verifyMediaFile(path) {
    try {
        return MessageMedia.fromFilePath(path);
    } catch (error) {
        th.error("Error al cargar el archivo de imagen:", error.message);
        throw error;
    }
}

async function sendWelcomeMessage(chatId, caption, mentions = []) {
    try {
        const media = verifyMediaFile(MEDIA_PATH);

        // Convertir las menciones en IDs serializados
        const mentionIds = mentions.map((contact) => contact.id._serialized);

        // Validar los datos antes de enviarlos
        th.info("Datos del mensaje:");
        th.info("Chat ID:", chatId);
        th.info("Menciones:", mentionIds);
        th.info("Caption:", caption);

        // Enviar el mensaje
        await sony.sendMessage(chatId, media, { caption, mentions: mentionIds });
        th.success(`Mensaje de bienvenida enviado al chat ${chatId}`);
    } catch (error) {
        th.error("Error al enviar el mensaje de bienvenida:", error.message);
    }
}

